INSERT INTO `migracion_log` (`IDMigracionLog`,`SentenciaSQL`,`FechaCreacion`) VALUES 
 (85,'SELECT \r\n	                            a.IDProducto,\r\n	                            a.CodigoProducto,\r\n	                            a.NombreProducto,\r\n                                CONCAT(a.CodigoProducto,a.NombreProducto) AS FullBusqueda,\r\n	                            a.Categoria,\r\n	                            a.SubCategoria,\r\n	                        IFNULL((SELECT SUM(c.CantidadEntrada) \r\n		                        FROM documentos_detalle c, documentos_encabezados d \r\n		                        WHERE c.IDDocumento = d.IDDocumento \r\n		                        AND c.IDProducto = a.IDProducto \r\n		                        AND d.FechaDocumento BETWEEN \'2018-10-04\' AND \'2018-10-04\'),0) as Entradas,\r\n	                        IFNULL((SELECT SUM(c.CantidadSalida) \r\n		                        FROM documentos_detalle c, documentos_encabezados d \r\n		                        WHERE c.IDDocumento = d.IDDocumento \r\n		                        AND c.IDProducto = a.IDProducto \r\n		                        AND d.FechaDocumento BETWEEN \'2018-10-04\' AND \'2018-10-04\'),0) as Salidas,\r\n	                        IFNULL((SELECT SUM(c.CantidadEntrada - c.CantidadSalida) \r\n		                        FROM documentos_detalle c, documentos_encabezados d \r\n		                        WHERE c.IDDocumento = d.IDDocumento \r\n		                        AND c.IDProducto = a.IDProducto \r\n		                        AND d.FechaDocumento BETWEEN \'2018-10-04\' AND \'2018-10-04\'),0) as Existencias,\r\n		                     a.UltimoCosto,\r\n		                     IFNULL((SELECT SUM(c.CantidadEntrada - c.CantidadSalida) \r\n		                        FROM documentos_detalle c, documentos_encabezados d \r\n		                        WHERE c.IDDocumento = d.IDDocumento \r\n		                        AND c.IDProducto = a.IDProducto \r\n		                        AND d.FechaDocumento BETWEEN \'2018-10-04\' AND \'2018-10-04\')*a.UltimoCosto,0) AS TotalInventario\r\n                            FROM producto_nombres a\r\n                            \r\n						    \r\n                            ORDER BY a.NombreProducto;','2018-10-04 18:40:08'),
 (86,'SELECT \r\n	                            a.IDProducto,\r\n	                            a.CodigoProducto,\r\n	                            a.NombreProducto,\r\n                                CONCAT(a.CodigoProducto,a.NombreProducto) AS FullBusqueda,\r\n	                            a.Categoria,\r\n	                            a.SubCategoria,\r\n	                        IFNULL((SELECT SUM(c.CantidadEntrada) \r\n		                        FROM documentos_detalle c, documentos_encabezados d \r\n		                        WHERE c.IDDocumento = d.IDDocumento \r\n		                        AND c.IDProducto = a.IDProducto \r\n		                        AND d.FechaDocumento BETWEEN \'2018-01-01\' AND \'2018-10-04\'),0) as Entradas,\r\n	                        IFNULL((SELECT SUM(c.CantidadSalida) \r\n		                        FROM documentos_detalle c, documentos_encabezados d \r\n		                        WHERE c.IDDocumento = d.IDDocumento \r\n		                        AND c.IDProducto = a.IDProducto \r\n		                        AND d.FechaDocumento BETWEEN \'2018-01-01\' AND \'2018-10-04\'),0) as Salidas,\r\n	                        IFNULL((SELECT SUM(c.CantidadEntrada - c.CantidadSalida) \r\n		                        FROM documentos_detalle c, documentos_encabezados d \r\n		                        WHERE c.IDDocumento = d.IDDocumento \r\n		                        AND c.IDProducto = a.IDProducto \r\n		                        AND d.FechaDocumento BETWEEN \'2018-01-01\' AND \'2018-10-04\'),0) as Existencias,\r\n		                     a.UltimoCosto,\r\n		                     IFNULL((SELECT SUM(c.CantidadEntrada - c.CantidadSalida) \r\n		                        FROM documentos_detalle c, documentos_encabezados d \r\n		                        WHERE c.IDDocumento = d.IDDocumento \r\n		                        AND c.IDProducto = a.IDProducto \r\n		                        AND d.FechaDocumento BETWEEN \'2018-01-01\' AND \'2018-10-04\')*a.UltimoCosto,0) AS TotalInventario\r\n        