 (413,'SELECT \r\n	                            a.IDDetalleDocumento,\r\n	                            a.IDDocumento,\r\n	                            b.NumeroDocumento,\r\n	                            b.FechaDocumento,\r\n	                            a.IDProducto,\r\n	                            a.CodigoProducto,\r\n	                            a.NombreProducto,\r\n	                            IFNULL(SUM(a.CantidadEntrada),0) AS CantidadEntrada,\r\n	                            a.PrecioEntrada,\r\n	                            IFNULL(SUM(a.CantidadSalida),0) AS CantidadSalida,\r\n	                            a.PrecioSalida,\r\n	                            (1.0000*0) AS Existencias,\r\n                                (1.0000*0) AS ValorExistencias,\r\n	                            a.UltimoCosto,\r\n	                            a.FormatoVenta,\r\n	                            a.IDBodega,\r\n	                            a.NombreBodega,\r\n	                            \'SALDO INICIAL\' AS Descripcion\r\n                            FROM documentos_detalle a,\r\n		                            documentos_encabezados b\r\n                            WHERE a.IDDocumento = b.IDDocumento\r\n                            AND a.IDProducto = \'1\'\r\n                            AND a.IDBodega = \'1\'\r\n                            AND b.FechaDocumento BETWEEN \'2018-10-01\' AND \'2018-10-31\'\r\n                            AND b.Operacion = \'INVENTARIO\'\r\n                            GROUP BY a.IDProducto\r\n\r\n                            UNION\r\n\r\n                            SELECT \r\n	                            a.IDDetalleDocumento,\r\n	                            a.IDDocumento,\r\n	                            b.NumeroDocumento,\r\n	                            b.FechaDocumento,	\r\n	                            a.IDProducto,\r\n	                            a.CodigoProducto,\r\n	                            a.NombreProducto,\r\n	                            a.CantidadEntrada,\r\n	                            a.PrecioEntrada,\r\n	                            a.CantidadSalida,\r\n	                            a.PrecioSalida,\r\n	                            (1.0000*0) AS Existencias,\r\n                                (1.0000*0) AS ValorExistencias,\r\n	                            a.UltimoCosto,\r\n	                            a.FormatoVenta,\r\n	                            a.IDBodega,\r\n	                            a.NombreBodega,	\r\n	                            \'MOVIMIENTOS\' AS Descripcion	\r\n                            FROM documentos_detalle a,\r\n		                            documentos_encabezados b\r\n                            WHERE a.IDDocumento = b.IDDocumento\r\n                            AND a.IDProducto = \'1\'\r\n                            AND a.IDBodega = \'1\'\r\n                            AND b.FechaDocumento BETWEEN \'2018-10-01\' AND \'2018-10-31\'\r\n                            AND b.Operacion NOT IN (\'INVENTARIO\')\r\n                            ORDER BY 4 ASC;','2018-10-04 19:37:34'),
 (414,'SELECT IDBodega,NombreBodega FROM bodegas_nombres a WHERE IDBodega > 0 ORDER BY IDBodega;','2018-10-04 19:37:56'),
 (415,'SELECT \r\n	                            a.IDProducto,\r\n	                            a.CodigoProducto,\r\n	                            a.NombreProducto,\r\n	                            a.Categoria,\r\n	                            a.SubCategoria,\r\n	                            a.Presentacion,\r\n	                            a.FormatoVenta,\r\n	                            a.UltimoCosto,\r\n                                b.IDBodega,\r\n	                            b.NombreBodega,\r\n	                            SUM(b.CantidadEntrada) AS Entradas,\r\n	                            SUM(b.CantidadSalida) AS Salidas,\r\n                                SUM(b.CantidadEntrada)-SUM(b.CantidadSalida) AS ExistenciaActual\r\n                            FROM producto_nombres a,\r\n                            		documentos_detalle b,\r\n                            		documentos_encabezados c\r\n                            WHERE a.IDProducto = b.IDProducto\r\n                            AND b.IDDocumento = c.IDDocumento\r\n                            \r\n                            AND c.FechaDocumento BETWEEN \'2018-10-01\' AND \'2018-10-31\'\r\n                            GROUP BY b.IDBodega,a.IDProducto\r\n                            ORDER BY a.NombreProducto;','2018-10-04 19:38:01'),
